import React from "react";
import Background from "./background.jpg";
import NavBar from "./navbar";
import Select from 'react-select';
import issues from "./data";
import api from "./api";

var stops = [];

class Register extends React.Component {

    state = {
            date: '',
            newAffect: '',
            searchBar: '',
            stops: '',
            type: undefined,
            chosenLine: undefined,
            departure: undefined,
            checkbox: undefined,
            multipleStops: undefined,
            dateFromTo: undefined,
            message: undefined,
            submit: undefined,
            departureSok: undefined,
        };

    componentDidMount() {
        var that = this;
        var date = new Date().getDate();
        var month = new Date().getMonth() + 1; if(month < 10){ month = "0"+month }
        var year = new Date().getFullYear();
        var hours = new Date().getHours(); if(hours < 10){ hours = "0"+hours }
        var min = new Date().getMinutes(); if(min < 10){ min = "0"+min }
        var sec = new Date().getSeconds(); if(sec < 10){ sec = "0"+sec }
        that.setState({
            date: year + '-' + month + '-' + date +'T' + hours + ':' + min + ':' + sec+".000+02:00",
        });

        setTimeout(() => {
            JSON.parse(localStorage.getItem("lines")).map((item, key) => (
                stops = stops.concat(item.quays)
            ))
        }, 800);
    }

    getIssuesCount = () => parseInt(JSON.stringify(issues.PtSituationElement.length));

    createNewIssue = event => {
        return {
            CreationTime: this.state.date,
            ParticipantRef: localStorage.getItem("loggedInAs"),
            SituationNumber: localStorage.getItem("loggedInAs") + ":SituationNumber:" + (this.getIssuesCount()+1),
            Source: {
                SourceType: "directReport",
            },
            Progress: "open",
            ValidityPeriod: {
                StartTime: event.target.from.value + ":00+02:00",
                EndTime: event.target.to.value + ":00+02:00",
            },
            Severity: "normal",
            ReportType: "incident",
            Summary: {
                "@Value": event.target.oppsummering.value,
                "@Language": "NO"
            },
            Description: {
                "@Value": event.target.beskrivelse.value,
                "@Language": "NO"
            },
            Affects: [],
        };
    };

    createNewLine = () => {
        return {
            Networks: { AffectedNetwork: { AffectedLine: { LineRef: this.state.chosenLine, Routes: null } } }
        }
    };

    createAffectedRoute = () => {
        return this.state.multipleStops.map(item => (
            { StopPoints: { AffectedStopPoint: { StopPointRef: item.value } } }
        ))
    };

    createNewSpecifiedStops = () => {
        return {
            Networks: { AffectedNetwork: { AffectedLine: { LineRef: this.state.chosenLine,
                        Routes: { AffectedRoute: this.createAffectedRoute() }
            } } }
        }
    };

    handleSubmit = (event) => {
        const newIssue = this.createNewIssue(event);

        if(this.state.type === "line"){
            if(this.state.checkbox){
                const newAffect = this.createNewSpecifiedStops();
                newIssue.Affects = newAffect;
            }else{
                const newAffect = this.createNewLine();
                newIssue.Affects = newAffect;
            }
        }
        if(this.state.type === "stop"){
            const newAffect = this.createAffectedRoute();
            newIssue.Affects = newAffect;
        }
        if(this.state.type === "departure"){
            console.log("to do");
        }else{
            const tmp = JSON.parse(localStorage.getItem("issues"));
            tmp.PtSituationElement.push(newIssue);
            localStorage.setItem('issues', JSON.stringify(tmp));
            this.props.history.push('/overview');
        }
    };

    handleChangeSpecifyStops = () => {
        const checkBox = document.getElementById("gridCheck");
        if (checkBox.checked === true){
            this.setState({
                checkbox: true,
            });
        }else{
            this.setState({
                checkbox: undefined,
            });
        }
    };

    handleClick = () => {
        this.props.history.push('/');
    };

    callApiDeparture = (event) => {
        this.setState({
            departureSok: true,
        });
        const date = document.getElementById("date").value;
        const authorities = localStorage.getItem("loggedInAs")+':Authority:'+localStorage.getItem("loggedInAs");
        const line = this.state.chosenLine;

        api.run('departure', authorities, line, date);
    };

    handleChangeType = (event) =>{
        this.setState({
            type: event.target.value,
            checkbox: undefined,
            chosenLine: undefined,
        });
        if(event.target.value === 'departure'){
            this.setState({
                departure: true,
            })
        }
    };

    handleChangeDate = (props) => {
        if(this.state.type !== 'departure'){
            this.setState({
                message: true,
                submit: true,
            });
        }
    };

    handleChangeLine = (event) => {
        if(this.state.type === 'departure'){
            this.setState({
                chosenLine: event.value,
            });
        }else{
            this.setState({
                chosenLine: event.value,
                dateFromTo: true,
            });
        }
    };

    handleChangeSpecifiedStops = (event) => {
        this.setState({ multipleStops: event, dateFromTo: true, });
    };

    getFromLocalParsed = (key) => {
        return JSON.parse(localStorage.getItem(key))
    };

    returnMappedObjects = (list) => {
        return list.map(item => (
            { label: item.name, value: item.id }
        ))
    };

    returnSpecifiedLines = () => {
        const tmp = JSON.parse(localStorage.getItem("lines")).find(item => (
            (item.id === this.state.chosenLine)
        ));

        if(tmp){
            return this.returnMappedObjects(tmp.quays);
        }
        return []
    };

    returnServiceJourney = () => {
        return JSON.parse(localStorage.getItem("departure")).map(item => (
            { label: 'Med avgang: '+item.departureTime, value: item.id }
        ))
    };

    render() {
        return (
            <div>
                <div className="navBar">
                    <img id="background" src={ Background } alt="" />
                    <NavBar />
                </div>
                <div className="register_box" id="small" >
                    <form className="register" onSubmit={this.handleSubmit}>
                        <h3 className="text-center text-white">Registrer ny melding</h3>
                        <br></br>
                        <div className="choose_type">
                            <p className="text-center text-white">Velg linje, stopp eller avgang</p>
                            <select className="browser-default custom-select" defaultValue={"default"} onChange={this.handleChangeType}>
                                <option value="default" disabled></option>
                                <option value="line">Linje</option>
                                <option value="stop">Stopp</option>
                                <option value="departure">Avgang</option>
                            </select>
                        </div>
                        <div className="choose_type">
                            {((this.state.type === "line") || (this.state.type === "departure")) &&
                            <Select placeholder="Velg linje"
                                onChange={this.handleChangeLine}
                                    options={this.returnMappedObjects(this.getFromLocalParsed("lines"))}
                            />
                            }
                            {(this.state.type === "line") && (this.state.chosenLine) &&
                            <div className="form-check d-flex justify-content-center">
                                <label className="form-check-label" htmlFor="gridCheck">
                                    <input className="form-check-input" type="checkbox" id="gridCheck" value="stops" onChange={this.handleChangeSpecifyStops} />
                                    <p className="text-center text-white">Gjelder avviket for spesifikke stopp?</p>
                                </label>
                            </div>
                            }
                            {(this.state.type === "line") && (this.state.chosenLine) && (this.state.checkbox) &&
                            <Select isMulti
                                    placeholder="Velg stopp"
                                    onChange={this.handleChangeSpecifiedStops}
                                    options={this.returnSpecifiedLines()}
                            />
                            }
                            {(this.state.type === "stop") &&
                            <Select isMulti
                                    placeholder="Velg stopp"
                                    onChange={this.handleChangeSpecifiedStops}
                                    options={this.returnMappedObjects(stops)}
                            />
                            }
                        </div>

                        {(this.state.departure) && (this.state.chosenLine)  &&
                        <div>
                            <br></br>
                            <p className="text-center text-white">Gyldighetsdato</p>
                            <input type="date" id="date" className="form-control" required/>
                            <button onClick={this.callApiDeparture} type="button" className="btn btn-primary btn-lg btn-block">SÃ¸k avganger</button>
                        </div>}

                        {(this.state.departureSok) && (this.state.chosenLine) &&
                        <div className="choose_type">
                            <br></br>
                            <p className="text-center text-white">Velg avgang</p>
                            <Select onChange={console.log("trykka")}
                                    options={ this.returnServiceJourney() }
                            />
                        </div>}

                        {(this.state.dateFromTo) &&
                        <div className="bd-highlight justify-content-center">
                            <br></br>
                            <p className="text-center text-white">Gyldighetsdato</p>
                            <div className="form-group d-flex">
                                <input type="datetime-local" name="from" id="from" className="form-control" required/>
                                <input onChange={this.handleChangeDate} type="datetime-local" name="to" id="to" className="form-control" required/>
                            </div>
                        </div>
                        }

                        {(this.state.message) &&
                        <div className="message">
                            <br></br>
                            <p className="text-center text-white">Melding</p>
                            <input type="String" name="oppsummering" className="form-control" placeholder="Oppsummering"
                                   required/>
                            <input type="String" name="beskrivelse" className="form-control" placeholder="Beskrivelse"/>
                        </div>
                        }
                        <br></br>
                        <br></br>
                        <div className="submit justify-content-center">
                            {(this.state.submit) &&
                            <button type="submit" className="btn btn-primary btn-lg btn-block">Registrer</button>}
                            <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Register;
