import React from "react";
import Background from "../img/background.jpg";
import NavBar from "./navbar";
import Select from 'react-select';
import api from "../api/api";
import 'flatpickr/dist/themes/material_blue.css';
import Flatpickr from 'react-flatpickr';

var stops = [];

class Register extends React.Component {

    state = {
            date: '',
            dateShort: '',
            newAffect: '',
            searchBar: '',
            stops: '',
            type: undefined,
            chosenLine: undefined,
            departure: undefined,
            checkbox: undefined,
            checkbox2: undefined,
            multiples: undefined,
            dateFromTo: undefined,
            message: undefined,
            submit: undefined,
            departureSok: undefined,
            reportType: 'general',
        };

    // This function creates a value with current time and stops
    componentDidMount() {
        var that = this;
        var date = new Date().getDate(); if(date < 10){ date = "0"+date }
        var month = new Date().getMonth() + 1; if(month < 10){ month = "0"+month }
        var year = new Date().getFullYear();
        var hours = new Date().getHours(); if(hours < 10){ hours = "0"+hours }
        var min = new Date().getMinutes(); if(min < 10){ min = "0"+min }
        var sec = new Date().getSeconds(); if(sec < 10){ sec = "0"+sec }
        that.setState({
            date: year + '-' + month + '-' + date +'T' + hours + ':' + min + ':' + sec+".000+02:00",
            dateShort: year + '-' + month + '-' + date +'T' + hours + ':' + min,
        });

        setTimeout(() => {
            JSON.parse(localStorage.getItem("lines")).map((item, key) => (
                stops = stops.concat(item.quays)
            ))
        }, 800);
    }

    // This function return the number of issues
    getIssuesCount = () => parseInt(JSON.parse(localStorage.getItem("issues")).PtSituationElement.length);

    // This function creates a new issue
    createNewIssue = event => {
        return {
            CreationTime: this.state.date,
            ParticipantRef: localStorage.getItem("loggedInAs"),
            SituationNumber: localStorage.getItem("loggedInAs") + ":SituationNumber:" + (parseInt(this.getIssuesCount())+1),
            Source: {
                SourceType: "directReport",
            },
            Progress: "open",
            ValidityPeriod: null,
            Severity: event.target.severity.value,
            ReportType: this.state.reportType,
            Summary: {
                "@Value": event.target.oppsummering.value,
                "@Language": "NO"
            },
            Description: {
                "@Value": event.target.beskrivelse.value,
                "@Language": "NO"
            },
            Affects: [],
        };
    };

    // This function creates objects for a new line
    createNewLine = () => {
        return {
            Networks: { AffectedNetwork: { AffectedLine: { LineRef: this.state.chosenLine, Routes: null } } }
        }
    };

    // This function creates objects for all specified stops for a line
    createAffectedRoute = () => {
        return this.state.multipleStops.map(item => (
            { StopPoints: { AffectedStopPoint: { StopPointRef: item.value } } }
        ))
    };

    // This function creates objects for a line with specified stops (calls createAffectedRoute)
    createNewSpecifiedStops = () => {
        return {
            Networks: { AffectedNetwork: { AffectedLine: { LineRef: this.state.chosenLine,
                        Routes: { AffectedRoute: this.createAffectedRoute() } } } }
        }
    };

    // This function creates objects for a new departure
    createAffectedDeparture = () => {
        return {
            VehicleJourneys: { AffectedVehicleJourney: { FramedVehicleJourneyRef: {
                DataFrameRef: document.getElementById("date").value, DatedVehicleJourneyRef: this.state.chosenLine }, Route: null }Â } }
    };

    // This function creates objects for a departure with specified stops (calls createAffectedDeparture)
    createNewSpecifiedStopsDeparture = () => {
        return {
            VehicleJourneys: { AffectedVehicleJourney: { FramedVehicleJourneyRef: {
                DataFrameRef: document.getElementById("date").value, DatedVehicleJourneyRef: this.state.chosenLine },
                    Route: this.createAffectedRoute() }Â }
        }
    };

    // This function checks if the app is creating data for a line, stop or departure
    handleSubmit = (event) => {

        const newIssue = this.createNewIssue(event);

        if(this.state.type === "departure"){
            newIssue.ValidityPeriod = {
                StartTime: event.target.date.value+":00+02:00",
                EndTime: event.target.date.value+":00+02:00"}
        }else{
            if(event.target.to.value){
                newIssue.ValidityPeriod = {
                    StartTime: event.target.from.value+":00+02:00",
                    EndTime: event.target.to.value+":00+02:00"}
            }else{
                newIssue.ValidityPeriod = {
                    StartTime: event.target.from.value+":00+02:00"}
            }
        }

        if(this.state.type === "line"){
            if(this.state.checkbox){
                const newAffect = this.createNewSpecifiedStops();
                newIssue.Affects = newAffect;
            }else{
                const newAffect = this.createNewLine();
                newIssue.Affects = newAffect;
            }
        }
        if(this.state.type === "stop"){
            const newAffect = this.createAffectedRoute();
            newIssue.Affects = newAffect;
        }
        if(this.state.type === "departure"){
            if(this.state.checkbox2){
                const newAffect = this.createNewSpecifiedStopsDeparture();
                newIssue.Affects = newAffect;
            }else{
                const newAffect = this.createAffectedDeparture();
                newIssue.Affects = newAffect;
            }
        }

        const tmp = JSON.parse(localStorage.getItem("issues"));
        tmp.PtSituationElement.push(newIssue);
        localStorage.setItem('issues', JSON.stringify(tmp));
        this.props.history.push('/overview');
    };

    handleChangeSpecifyStopsLine = () => {
        const checkBox = document.getElementById("gridCheck");
        if (checkBox.checked === true){
            this.setState({
                checkbox: true,
            });
        }else{
            this.setState({
                checkbox: undefined,
            });
        }
    };

    handleChangeSpecifyStopsDeparture = () => {
        const checkBox = document.getElementById("gridCheck2");
        if (checkBox.checked === true){
            this.setState({
                checkbox2: true,
            });
        }else{
            this.setState({
                checkbox2: undefined,
            });
        }
    };

    handleClick = () => {
        this.props.history.push('/');
    };

    callApiDeparture = (event) => {
        const date = document.getElementById("date").value;
        const authorities = localStorage.getItem("loggedInAs")+':Authority:'+localStorage.getItem("loggedInAs");
        const line = this.state.chosenLine;

        api.run('departure', authorities, line, date);

        setTimeout(() => {
            this.setState({
                departureSok: true,
            });
        }, 1200);

    };

    handleChangeType = (event) =>{
        if(this.state.departureSok){
            this.setState({
                message: undefined,
            });
        }
        this.setState({
            type: event.target.value,
            checkbox: undefined,
            checkbox2: undefined,
            chosenLine: undefined,
            departureSok: undefined,
            submit: undefined,
            departure: undefined,
        });
        if(event.target.value === 'departure'){
            this.setState({
                departure: true,
                dateFromTo: undefined,
                message: undefined,
            })
        }
    };

    handleChangeLine = (event) => {
        if(this.state.type === 'departure'){
            this.setState({
                chosenLine: event.value,
            });
        }else{
            this.setState({
                chosenLine: event.value,
                dateFromTo: true,
                message: true,
                submit: true,
            });
        }
    };

    handleChangeReportType = () => {
        const checkBox = document.getElementById("gridCheck3");
        if (checkBox.checked === true){
            this.setState({
                reportType: 'incident',
            });
        }else{
            this.setState({
                reportType: 'general',
            });
        }
    };

    handleChangeDeparture = (event) => {
        this.setState({
            chosenLine: event.value,
            message: true,
            submit: true,
        });
    };

    handleChangeSpecifiedStops = (event) => {
        this.setState({
            multipleStops: event,
            dateFromTo: true,
            message: true,
            submit: true,
        });
    };

    handleChangeSpecifiedStopsDeparture = (event) => {
        this.setState({ multipleStops: event });
    };

    getFromLocalParsed = (key) => {
        return JSON.parse(localStorage.getItem(key))
    };

    returnMappedObjects = (list) => {
        return list.map(item => (
            { label: item.name, value: item.id }
        ))
    };

    returnSpecifiedLines = () => {
        const tmp = JSON.parse(localStorage.getItem("lines")).find(item => (
            (item.id === this.state.chosenLine)
        ));

        if(tmp){
            return this.returnMappedObjects(tmp.quays);
        }
        return []
    };

    returnSpecifiedLinesDeparture = () => {
        const tmp = JSON.parse(localStorage.getItem("departure")).find(item => (
            (item.id === this.state.chosenLine)
        ));

        if(tmp){
            return tmp.estimatedCalls.map(item => (
                { label: item.quay.name.replace("+01.00",""), value: item.quay.id }
            ))
        }
        return []
    };

    returnServiceJourney = () => {
        return JSON.parse(localStorage.getItem("departure")).map(item => (
            { label: item.estimatedCalls[0].aimedDepartureTime.split("T").pop()+" fra "+item.estimatedCalls[0].quay.name,
                value: item.id
            }
        )).sort((a, b) => (a.label > b.label) ? 1 : -1)

    };

    render() {
        return (
            <div>
                <div className="navBar">
                    <img id="background" src={ Background } alt="" />
                    <NavBar />
                </div>
                <div className="register_box" id="small" >
                    <form className="register" onSubmit={this.handleSubmit}>
                        <h3 className="text-center text-white">Registrer ny melding</h3>
                        <br></br>
                        <div className="choose_type">
                            <p className="text-center text-white">Velg linje, stopp eller avgang</p>
                            <select className="browser-default custom-select" defaultValue={"default"} onChange={this.handleChangeType}>
                                <option value="default" disabled></option>
                                <option value="line">Linje</option>
                                <option value="stop">Stopp</option>
                                <option value="departure">Avgang</option>
                            </select>
                        </div>
                        <div className="choose_type">

                            {((this.state.type === "line") || (this.state.type === "departure")) &&
                            <Select placeholder="Velg linje"
                                onChange={this.handleChangeLine}
                                    options={this.returnMappedObjects(this.getFromLocalParsed("lines"))}
                            />
                            }

                            {(this.state.type === "line") && (this.state.chosenLine) &&
                            <div className="form-check d-flex">
                                <label className="form-check-label" htmlFor="gridCheck">
                                    <input className="form-check-input" type="checkbox" id="gridCheck" value="stops" onChange={this.handleChangeSpecifyStopsLine} />
                                    <p className="text-center text-white">Gjelder avviket for spesifikke stopp?</p>
                                </label>
                            </div>
                            }

                            {(this.state.type === "line") && (this.state.chosenLine) && (this.state.checkbox) &&
                            <div><Select isMulti
                                    placeholder=" "
                                    onChange={this.handleChangeSpecifiedStops}
                                    options={this.returnSpecifiedLines()}
                            /><br></br></div>
                            }

                            {(this.state.type === "stop") &&
                            <div>
                                <Select isMulti
                                        placeholder="Velg stopp"
                                        onChange={this.handleChangeSpecifiedStops}
                                        options={this.returnMappedObjects(stops)}
                                />
                                <br></br>
                            </div>
                            }
                        </div>

                        {(this.state.departure) && (this.state.chosenLine)  &&
                        <div>
                            <br></br>
                            <p className="text-center text-white">Gyldighetsdato</p>
                            <Flatpickr id='date'
                                       name='to'
                                       className="form-control"
                                       options={ {
                                           enableTime: false,
                                           minDate: "2020-01-15",
                                           dateFormat: "Y-m-d",}}
                            />
                            <button onClick={this.callApiDeparture} type="button" className="btn btn-primary btn-lg btn-block">SÃ¸k avganger</button>
                        </div>}

                        {(this.state.departureSok) && (this.state.chosenLine) &&
                        <div className="choose_type">
                            <br></br>
                            <p className="text-center text-white">Velg avgang</p>
                            <Select placeholder=" "
                                onChange={this.handleChangeDeparture}
                                    options={ this.returnServiceJourney() }
                            />
                        </div>}

                        {(this.state.departure) && (this.state.message) &&
                        <div className="form-check d-flex">
                            <label className="form-check-label" htmlFor="gridCheck">
                                <input className="form-check-input" type="checkbox" id="gridCheck2" value="stops" onChange={this.handleChangeSpecifyStopsDeparture} />
                                <p className="text-center text-white">Gjelder avviket for spesifikke stopp?</p>
                            </label>
                        </div>
                        }

                        {(this.state.departure) && (this.state.checkbox2) &&
                            <div>
                                <Select isMulti
                                        placeholder=" "
                                        onChange={this.handleChangeSpecifiedStopsDeparture}
                                        options={ this.returnSpecifiedLinesDeparture() }
                                />
                                <br></br>
                            </div>
                        }

                        {(this.state.dateFromTo) &&
                        <div className="bd-highlight justify-content-center">
                            <p className="text-center text-white">Gyldighetsdato</p>
                            <div className="form-group d-flex">
                                <Flatpickr data-enable-time
                                           value={this.state.dateShort}
                                           name='from'
                                           className="form-control"
                                           options={ {
                                               enableTime: true,
                                               dateFormat: "Y-m-d H:i",
                                               minDate: this.state.dateShort,
                                               time_24hr: true}}
                                />
                                <Flatpickr id='date-to' data-enable-time
                                           name='to'
                                           placeholder='Til-dato'
                                           className="form-control"
                                           options={ {
                                               enableTime: true,
                                               dateFormat: "Y-m-d H:i",
                                               time_24hr: true}}
                                />
                            </div>
                        </div>
                        }

                        {(this.state.message) &&
                            <div>
                                <div className="severity&type">
                                    <p className="text-center text-white">Alvorlighetsgrad</p>
                                    <select className="form-control" defaultValue={"normal"} name="severity">
                                        <option value="noImpact">Ingen</option>
                                        <option value="slight">Noe</option>
                                        <option value="normal">Normal</option>
                                        <option value="severe">Kritisk</option>
                                    </select>
                                    <div className="form-check d-flex">
                                        <label className="form-check-label" htmlFor="gridCheck3">
                                            <input className="form-check-input" type="checkbox" id="gridCheck3" value="incident" onChange={this.handleChangeReportType} />
                                            <p className="text-center text-white">Er dette en ulykke?</p>
                                        </label>
                                    </div>
                                </div>
                                <div className="message">
                                    <p className="text-center text-white">Melding</p>
                                    <input type="String" name="oppsummering" className="form-control" placeholder="Oppsummering"
                                           required/>
                                    <input type="String" name="beskrivelse" className="form-control" placeholder="Beskrivelse"/>
                                </div>
                            </div>
                        }
                        <br></br>
                        <br></br>
                        <div className="submit justify-content-center">
                            {(this.state.submit) &&
                            <button type="submit" className="btn btn-primary btn-lg btn-block">Registrer</button>}
                            <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Register;
