import React from "react";
import Background from "./background.jpg";
import NavBar from "./navbar";

class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            date: '',
            newAffect: '',
            searchBar: '',
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        var that = this;
        var date = new Date().getDate(); //Current Date
        var month = new Date().getMonth() + 1; //Current Month
        if(month < 10){ month = "0"+month }
        var year = new Date().getFullYear(); //Current Year
        var hours = new Date().getHours(); //Current Hours
        if(hours < 10){ hours = "0"+hours }
        var min = new Date().getMinutes(); //Current Minutes
        if(min < 10){ min = "0"+min }
        var sec = new Date().getSeconds(); //Current Seconds
        if(sec < 10){ sec = "0"+sec }
        that.setState({
            //Setting the value of the date time
            date:
                year + '-' + month + '-' + date +'T' + hours + ':' + min + ':' + sec+".000+02:00",
        });
    }

    handleSubmit(event) {
        const newIssue = {
                CreationTime: this.state.date,
                ParticipantRef: localStorage.getItem("loggedInAs"),
                SituationNumber: localStorage.getItem("loggedInAs") + ":SituationNumber:" + (parseInt(localStorage.getItem("countIssues")) + 1),
                Source: {
                    SourceType: "directReport",
                },
                Progress: "open",
                ValidityPeriod: {
                    StartTime: event.target.from.value + "+02:00",
                    EndTime: event.target.to.value + "+02:00",
                },
                Severity: "normal",
                ReportType: "incident",
                Summary: {
                    "@Value": event.target.oppsummering.value,
                    "@Language": "NO"
                },
                Description: {
                    "@Value": event.target.beskrivelse.value,
                    "@Language": "NO"
                },
                Affects: [],
            };

        localStorage.setItem('countIssues', (parseInt(localStorage.getItem("countIssues"))+1));

        const type = localStorage.getItem("type");
        if(type === "Linje"){
            let newAffect = {
                Networks: {
                    AffectedNetwork: {
                        AffectedLine: {
                            LineRef: localStorage.getItem("chosen"),
                            Routes: null
                        }
                    }
                }
            };
            newIssue.Affects.push(newAffect);
        }

        const tmp = JSON.parse(localStorage.getItem("issues"));
        tmp.PtSituationElement.push(newIssue);
        localStorage.setItem('issues', JSON.stringify(tmp));
        this.props.history.push('/overview');
    }

    handleClick(event) {
        this.props.history.push('/home');
    }

    handleType(){
        const type = localStorage.getItem("type");
        if(type === "Linje"){
            this.setState({
                searchBar:
                    <div>
                        <select className="browser-default custom-select" defaultValue={"default"} onChange={this.handleChange2} name="type">
                            <option value="default" disabled></option>
                            {JSON.parse(localStorage.getItem("lines")).map((item, key) => (
                                <option key={key} value={item.id}>{item.name}</option>
                                ))}
                        </select>
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" id="gridCheck1" />
                                <label className="form-check-label" htmlFor="gridCheck1">
                                    <h3 className="text-primary">Gjelder avviket for hele linjen?</h3> </label>
                        </div>
                    </div>
            });
        }
        if(type === "Stopp"){
            this.setState({
                searchBar:
                    <select className="browser-default custom-select" onChange={this.handleChange2} name="type" searchable="SÃ¸k her..">
                        <option value="default" disabled></option>
                        {JSON.parse(localStorage.getItem("stops")).map((item, key) => (
                            <option key={key} value={item.id}>{item.name}</option>
                        ))}
                    </select>
            });
        }
    }

    handleChange(event) {
        localStorage.setItem('type', event.target.value);
        this.handleType();
    }

    handleChange2(event) {
        localStorage.setItem('chosen', event.target.value);
    }

    render() {
        return (
            <div>
                <div className="navBar">
                    <img id="background" src={ Background } alt="" />
                    <NavBar />
                </div>
                <div className="register_box" id="small" >
                    <form className="register" onSubmit={this.handleSubmit}>
                        <h3 className="text-center text-white">Registrer ny melding</h3>
                        <div className="form-row d-flex flex-row bd-highlight mb-2 justify-content-center">
                            <div className="fromDate p-2 flex-grow-1">
                                <p className="text-center text-white">Fra</p>
                                <input type="datetime-local" step="1" name="from" className="form-control" required/>
                            </div>
                            <div className="toDate p-2 flex-grow-1">
                                <p className="text-center text-white">Til</p>
                                <input type="datetime-local" step="1" name="to" className="form-control" required/>
                            </div>
                        </div>
                        <div className="message">
                            <p className="text-center text-white">Melding</p>
                            <input type="String" name="oppsummering" className="form-control" placeholder="Oppsummering" required/>
                            <input type="String" name="beskrivelse" className="form-control" placeholder="Beskrivelse"/>
                        </div>
                        <br></br>
                        <div className="choose_type">
                            <p className="text-center text-white">Velg linje, stopp eller avgang</p>
                            <select className="browser-default custom-select" defaultValue={"default"} onChange={this.handleChange}>
                                <option value="default" disabled></option>
                                <option value="Linje">Linje</option>
                                <option value="Stopp">Stopp</option>
                                <option value="Avgang">Avgang</option>
                            </select>
                        </div>
                        <div className="choose_type">
                            {this.state.searchBar}
                        </div>
                        <br></br>
                        <br></br>
                        <div className="submit justify-content-center">
                            <button type="submit" className="btn btn-primary btn-lg btn-block">Registrer</button>
                            <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Register;
