import React from "react";
import SearchField from "./searchField";
import Background from "./background.jpg";
import NavBar from "./navbar";
import issues from './data';

class Register extends React.Component {
    constructor(props) {
        super(props);
        localStorage.setItem("type", "Linje");

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleClick = this.handleClick.bind(this);
    }

    handleChange(event) {
        localStorage.setItem('type', event.target.value);
    }

    handleSubmit(event) {
        window.alert(event.target.from.value+" til"+event.target.to.value);
        this.props.history.push('/');
    }

    handleClick(event) {
        this.props.history.push('/');
    }

    render() {
        return (
            <div>
                <img id="background" src={ Background } alt="" />
                <div className="navBar">
                </div>
                <NavBar />
                <div className="register_box" >
                    <form className="register" onSubmit={this.handleSubmit}>
                        <h3 className="text-center text-white">Registrer ny melding</h3>
                        <div className="form-row d-flex flex-row bd-highlight mb-2 justify-content-center">
                            <div className="fromDate p-2 flex-grow-1">
                                <p className="text-center text-white">Fra</p>
                                <input type="datetime-local" name="from" className="form-control" required/>
                            </div>
                            <div className="toDate p-2 flex-grow-1">
                                <p className="text-center text-white">Til</p>
                                <input type="datetime-local" name="to" className="form-control" required/>
                            </div>
                        </div>
                        <div className="message">
                            <p className="text-center text-white">Melding</p>
                            <input type="String" className="form-control" placeholder="Oppsummering" required/>
                            <input type="String" className="form-control" placeholder="Beskrivelse"/>
                        </div>
                        <br></br>
                        <div className="choose_type">
                            <p className="text-center text-white">SÃ¸k linje, stopp eller avgang</p>
                            <select className="browser-default custom-select" value={localStorage.getItem('type')} onChange={this.handleChange}>
                                <option value="Linje">Linje</option>
                                <option value="Stopp">Stopp</option>
                                <option value="Avgang">Avgang</option>
                            </select>
                        </div>
                        <input type="String" className="form-control" placeholder="Beskrivelse" required/>
                        <br></br>
                        <br></br>
                        <div className="submit justify-content-center">
                            <button type="submit" className="btn btn-primary btn-lg btn-block">Registrer</button>
                            <button onClick={this.handleClick} type="submit" className="btn btn-warning btn-lg btn-block">Tilbake</button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Register;
